import React, { Component } from "react";
import { connect } from "react-redux";
import { userActions } from "_actions";
import "_asset/css/style.css";

class LoginPage extends Component {
  constructor(props) {
    super(props);

    // reset login status
    this.props.dispatch(userActions.logout());

    this.state = {
      username: "",
      password: "",
      submitted: false
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(e) {
    const { name, value } = e.target;
    this.setState({ [name]: value });
  }

  handleSubmit(e) {
    e.preventDefault();

    this.setState({ submitted: true });
    const { username, password } = this.state;
    const { dispatch } = this.props;

    if (username && password) {
      dispatch(userActions.login(username, password));
    }
  }

  render() {
    const { loggingIn } = this.props;
    const { username, password, submitted } = this.state;

    return (
      <div className="login_wrapper">
        <div className="login_container">
          <h1>Welcome H-GMS</h1>
          <form className="form" name="form" onSubmit={this.handleSubmit}>
            <input
              type="text"
              className="basic"
              name="username"
              value={username}
              onChange={this.handleChange}
              title="E-mail 주소를 입력하세요."
              placeholder="E-mail"
            />
            <input
              type="password"
              className="basic"
              name="password"
              value={password}
              onChange={this.handleChange}
              title="비밀번호를 입력하세요."
              placeholder="Password"
            />
            <button type="submit" id="login-button" title="로그인">
              로그인
            </button>
            {loggingIn && (
              <img src="data:image/gif;base64," />
            )}
            {/* <div className="etc_area">
              <div className="alias_id">
                <input type="checkbox" id="AliasName" />
                <label for="AliasName" title="이메일 주소 저장">
                  이메일 주소 저장
                </label>
              </div>
            </div> */}
          </form>
        </div>
      </div>
    );
  }
}

function mapStateToProps(state) {
  const { loggingIn } = state.authentication;
  return {
    loggingIn
  };
}

const connectedLoginPage = connect(mapStateToProps)(LoginPage);
export { connectedLoginPage as LoginPage };
